import { Fragment, useEffect } from "react";
import { useNavigate, useParams } from "react-router";
import * as yup from "yup";
import { useForm, SubmitHandler } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";

import { useAppDispatch, useAppSelector } from "redux/hooks";
import { getCurrentMovie } from "redux/Movie/slice";
import { setEmail } from "redux/Auth/slice";
import { Button, Form, FormGroup, Input, Label } from "reactstrap";
import { IFormValues } from "types/types";

const schema = yup
  .object({
    email: yup
      .string()
      .required("this field is required")
      .matches(
        /^[a-zA-Z0-9]+@(?:[a-zA-Z0-9]+\.)+[A-Za-z]+$/,
        "Enter a valid Email Address"
      ),
    password: yup
      .string()
      .required("this field is required")
      .min(4, "Password is too short - should be 4 chars minimum."),
  })
  .required();

const EditMoviePage = () => {
  const currentMovie = useAppSelector((state) => state.Movie.currentMovie);
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<IFormValues>({
    reValidateMode: "onChange",
    defaultValues: {
      name: currentMovie?.name,
      description: currentMovie?.description,
    },
    resolver: yupResolver(schema),
    shouldFocusError: true,
  });

  const loading = useAppSelector((state) => state.Common.loading);
  const email = useAppSelector((state) => state.Auth.email);

  const params = useParams();

  const navigate = useNavigate();

  const dispatch = useAppDispatch();

  useEffect(() => {
    (!currentMovie || currentMovie?.id.toString() !== params.id) &&
      !loading &&
      dispatch(getCurrentMovie(params.id));
    if (!email) {
      const localStorageEmail = localStorage.getItem("email");
      localStorageEmail && dispatch(setEmail(localStorageEmail));
    }
  }, [params.id]);

  const submitForm = () => {
    console.log("submitForm");
  };
  console.log({ currentMovie });

  if (currentMovie)
    return (
      <Form onSubmit={handleSubmit(submitForm)}>
        <FormGroup>
          <Label for="name">Name</Label>
          <Input
            id="name"
            type="text"
            {...register("name", { required: true })}
          />
        </FormGroup>
        <FormGroup>
          <Label for="description">Description</Label>
          <Input
            id="description"
            type="text"
            {...register("description", { required: true })}
          />
        </FormGroup>
        <Button>Submit</Button>
      </Form>
    );
  return null;
};
export default EditMoviePage;
