import { Fragment, useEffect } from "react";
import { useNavigate, useParams } from "react-router";
import * as yup from "yup";
import { useForm, SubmitHandler, Controller } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";

import { useAppDispatch, useAppSelector } from "redux/hooks";
import { setEmail } from "redux/Auth/slice";
import {
  Button,
  Form,
  FormFeedback,
  FormGroup,
  Input,
  Label,
} from "reactstrap";
import { IFormValues } from "types/types";
import classes from "./MovieForm.module.scss";

const schema = yup
  .object({
    email: yup
      .string()
      .required("this field is required")
      .matches(
        /^[a-zA-Z0-9]+@(?:[a-zA-Z0-9]+\.)+[A-Za-z]+$/,
        "Enter a valid Email Address"
      ),
    password: yup
      .string()
      .required("this field is required")
      .min(4, "Password is too short - should be 4 chars minimum."),
  })
  .required();

interface Iprops {
  onSubmit: SubmitHandler<IFormValues>;
  initValue?: IFormValues | undefined;
}

const MovieForm = (props: Iprops) => {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm<IFormValues>({
    reValidateMode: "onChange",
    defaultValues: {
      name: props.initValue?.name,
      description: props.initValue?.description,
    },
    resolver: yupResolver(schema),
    shouldFocusError: true,
  });

  console.log(props.initValue, { errors });

  return (
    <Form onSubmit={handleSubmit(props.onSubmit)}>
      <FormGroup>
        <Label for="name">Name</Label>

        <Controller
          name="name"
          control={control}
          render={({ field }) => <Input {...field} invalid={!!errors.name} />}
        />
        <FormFeedback>{errors.name?.message}</FormFeedback>
      </FormGroup>
      <FormGroup>
        <Label for="description">Description</Label>

        <Controller
          control={control}
          name="description"
          render={({ field }) => (
            <Input {...field} invalid={!!errors.description} />
          )}
        />
        <FormFeedback>{errors.description?.message}</FormFeedback>
      </FormGroup>
      <Button>Submit</Button>
    </Form>
  );
};
export default MovieForm;
