import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { IUserLoginForm } from "types";

type TinitState = {
  loading: boolean;
  showAlert: boolean;
  messageAlert: string;
  typeAlert: string;
  redirectState: boolean;
  redirectURL: string;
};
const initialState: TinitState = {
  loading: false,
  //Alert
  showAlert: false,
  messageAlert: "",
  typeAlert: "",
  //Redirect
  redirectState: false,
  redirectURL: "",
};
const commonSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {
    registerUser: (state, action: PayloadAction<IUserLoginForm>) => {},
    loginUser: (state, action: PayloadAction<IUserLoginForm>) => {},
  },
});
export const { registerUser } = commonSlice.actions;

export default commonSlice.reducer;
