import Button from "components/common/Button";
import MovieCard from "components/common/MovieCard";
import { IMovie } from "types/types";
import classes from "./CurrentMoviePage.module.scss";

interface Iprops {
  currentMovie: IMovie;
  onDelete: (id: number) => void;
  onEdit: (id: number) => void;
}
const CurrentMoviePage = (props: Iprops) => {
  return (
    <section className={classes.Wrapper}>
      <h1>{props.currentMovie.name}</h1>
      <p> {props.currentMovie.description}</p>
      <div>
        <p>creator: {props.currentMovie.creator}</p>
        <p>release date: {props.currentMovie.releaseDate || "relea"}</p>
      </div>
      <div>
        <Button onClick={() => props.onDelete(props.currentMovie.id)}>
          Delete
        </Button>
        <Button onClick={() => props.onEdit(props.currentMovie.id)}>
          Edit
        </Button>
      </div>
    </section>
  );
};

export default CurrentMoviePage;
