import { useForm, SubmitHandler } from "react-hook-form";
import { useState } from "react";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

import { IUserLoginForm } from "types/types";
import classes from "./RegisterForm.module.scss";
import Button from "components/common/Button";
import { Form, Input, Label } from "reactstrap";

interface Iprops {
  onSubmit: SubmitHandler<IUserLoginForm>;
}

const schema = yup
  .object({
    email: yup
      .string()
      .required("this field is required")
      .matches(
        /^[a-zA-Z0-9]+@(?:[a-zA-Z0-9]+\.)+[A-Za-z]+$/,
        "Enter a valid Email Address"
      ),
    password: yup
      .string()
      .required("this field is required")
      .min(4, "Password is too short - should be 4 chars minimum."),
  })
  .required();

const RegisterForm = (props: Iprops) => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<IUserLoginForm>({
    reValidateMode: "onChange",
    resolver: yupResolver(schema),
    shouldFocusError: true,
    criteriaMode: "firstError",
    shouldUnregister: true,
  });
  const [passwordShown, setPasswordShown] = useState<boolean>(false);

  const onSubmit = async (data: IUserLoginForm) => {
    props.onSubmit(data);
  };
  // console.log(errors, "ERR");

  return (
    <Form className={classes.form} onSubmit={handleSubmit(onSubmit)}>
      <Label htmlFor="email">
        Email Address:
        <Input
          {...register("email", { required: true })}
          autoComplete="email"
          id="email"
          invalid={!!errors.email}
        />
        {errors.email && <p>{errors.email.message}</p>}
      </Label>
      <Label htmlFor="password">
        Password:
        <Input
          {...register("password", { required: true })}
          autoComplete="current-password"
          type={passwordShown ? "text" : "password"}
        />
        <span onClick={() => setPasswordShown(!passwordShown)}>show</span>
        {errors.password && <p>{errors.password.message}</p>}
      </Label>

      <Button type="submit">Submit</Button>
    </Form>
  );
};

export default RegisterForm;
