import Button from "components/common/Button";
import SearchBox from "components/common/SearchBox";
import { useRef } from "react";
import { useNavigate, NavLink as Link } from "react-router-dom";
import {
  Collapse,
  Nav,
  Navbar,
  NavbarBrand,
  NavbarToggler,
  NavItem,
  NavLink,
} from "reactstrap";
import { useAppDispatch } from "redux/hooks";
import { setSearchText } from "redux/Movie/slice";
import { isAuth } from "util/helpers";
import classes from "./Layout.module.scss";

const Header = () => {
  const navigate = useNavigate();
  const inputRef = useRef<HTMLInputElement>(null);

  const dispatch = useAppDispatch();
  const onSearch = () => {
    !!inputRef.current && dispatch(setSearchText(inputRef.current.value));
  };

  const logout = () => {
    localStorage.removeItem("Mtoken");
    localStorage.removeItem("email");
    navigate("/login");
  };
  return (
    <header className={classes.Wrapper}>
      <Navbar color="dark" expand="sm" dark fixed="top">
        <NavbarBrand href="/">Movie App</NavbarBrand>
        <NavbarToggler
          onClick={function noRefCheck() {
            console.log("NavbarToggler");
          }}
        />
        <Collapse navbar>
          <Nav className="me-auto" navbar>
            <NavItem></NavItem>
          </Nav>
        </Collapse>
        {isAuth() && (
          <NavItem>
            <NavLink
              tag={Link}
              className={({ isActive }) =>
                isActive ? classes.active : undefined
              }
              to="/"
              end
            >
              Home
            </NavLink>

            <NavLink
              tag={Link}
              // className={({ isActive }) =>
              //   isActive ? classes.active : undefined
              // }
              to="/add-movie"
            >
              add movie
            </NavLink>
          </NavItem>
        )}
        {!isAuth() && (
          <>
            <NavLink
              tag={Link}
              // className={({ isActive }) =>
              //   isActive ? classes.active : undefined
              // }
              to="/login"
            >
              login
            </NavLink>

            <NavLink
              tag={Link}
              // className={({ isActive }) =>
              //   isActive ? classes.active : undefined
              // }
              to="/register"
            >
              register
            </NavLink>
          </>
        )}
      </Navbar>
      <div className={classes.searchWrapper}>
        {isAuth() && <SearchBox ref={inputRef} onChange={onSearch} />}
        {isAuth() && (
          <Button color="danger" onClick={logout}>
            Logout
          </Button>
        )}
      </div>
    </header>
  );
};

export default Header;
