import Button from "components/common/Button";
import SearchBox from "components/common/SearchBox";
import { useRef } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import {
  Collapse,
  Nav,
  Navbar,
  NavbarBrand,
  NavbarToggler,
  NavItem,
} from "reactstrap";
import { useAppDispatch } from "redux/hooks";
import { setSearchText } from "redux/Movie/slice";
import { isAuth } from "util/helpers";
import classes from "./Layout.module.scss";

const Header = () => {
  const navigate = useNavigate();
  const inputRef = useRef<HTMLInputElement>(null);

  const [isOpen, setIsOpen] = useState<boolean>(false);

  const dispatch = useAppDispatch();
  const onSearch = () => {
    !!inputRef.current && dispatch(setSearchText(inputRef.current.value));
  };
  const toggle = () => {
    // this.setState({
    //   isOpen: !this.state.isOpen
    // });
  };
  const logout = () => {
    localStorage.removeItem("Mtoken");
    localStorage.removeItem("email");
    navigate("/login");
  };
  return (
    <header className={classes.Wrapper}>
      <Navbar color="dark" expand="sm" dark fixed="top">
        <NavbarBrand href="/">Movie App</NavbarBrand>
        <NavbarToggler
          className="me-2"
          onClick={function noRefCheck() {
            console.log("NavbarToggler");
          }}
        />
        <Collapse navbar>
          <Nav className="me-auto" navbar>
            <NavItem></NavItem>
          </Nav>
        </Collapse>
        {isAuth() && (
          <>
            <NavItem>
              <NavLink
                className={({ isActive }) =>
                  isActive ? classes.active : undefined
                }
                to="/"
                end
              >
                Home
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={({ isActive }) =>
                  isActive ? classes.active : undefined
                }
                to="/add-movie"
              >
                add movie
              </NavLink>
            </NavItem>
          </>
        )}
        {!isAuth() && (
          <>
            <NavItem>
              <NavLink
                className={({ isActive }) =>
                  isActive ? classes.active : undefined
                }
                to="/login"
              >
                login
              </NavLink>
            </NavItem>

            <NavItem>
              <NavLink
                className={({ isActive }) =>
                  isActive ? classes.active : undefined
                }
                to="/register"
              >
                register
              </NavLink>
            </NavItem>
          </>
        )}
        {isAuth() && (
          <NavItem className={classes.searchWrapper}>
            <SearchBox ref={inputRef} onChange={onSearch} />
            <Button color="danger" onClick={logout}>
              Logout
            </Button>
          </NavItem>
        )}
      </Navbar>
    </header>
  );
};

export default Header;
