import { useForm, SubmitHandler } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

import { IUserRegisterForm } from "types";
import classes from "./RegisterForm.module.scss";
import Button from "components/common/Button";
import { useAppDispatch } from "redux/hooks";
import { registerUser } from "redux/Auth/slice";
import { useState } from "react";

interface Iprops {
  onSubmit: SubmitHandler<IUserRegisterForm>;
}

const schema = yup
  .object({
    email: yup
      .string()
      .required("this field is required")
      .matches(
        /^[a-zA-Z0-9]+@(?:[a-zA-Z0-9]+\.)+[A-Za-z]+$/,
        "Enter a valid Email Address"
      ),
    password: yup
      .string()
      .required("this field is required")
      .min(8, "Password is too short - should be 8 chars minimum."),
    confirmPassword: yup
      .string()
      .required("this field is required")
      .min(8, "Password is too short - should be 8 chars minimum.")
      .oneOf([yup.ref("password"), null], "Passwords must match"),
  })
  .required();

const RegisterForm = (props: Iprops) => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<IUserRegisterForm>({
    resolver: yupResolver(schema),
    shouldFocusError: true,
  });
  const [passwordShown, setPasswordShown] = useState<boolean>(false);
  const [confirmPasswordShown, setconfirmPasswordShown] =
    useState<boolean>(false);

  const dispatch = useAppDispatch();

  const onSubmit = async (data: IUserRegisterForm) => {
    console.log(data, "data");
    dispatch(registerUser({ ...data }));
    console.log(yup.ref("password"), "yup");
  };
  // console.log(errors, "ERR");

  return (
    <form className={classes.form} onSubmit={handleSubmit(onSubmit)}>
      <label htmlFor="email">Email Address:</label>
      <input {...register("email", { required: true })} id="email" />
      {errors.email && <p>{errors.email.message}</p>}
      <label htmlFor="password">Password:</label>
      <input {...register("password", { required: true })} type="password" />
      <span onClick={() => setPasswordShown(!passwordShown)}>show</span>
      {errors.password && <p>{errors.password.message}</p>}
      <label htmlFor="confirmPassword">Password Retype:</label>
      <input
        {...register("confirmPassword", { required: true })}
        type="password"
      />
      <span onClick={() => setPasswordShown(!passwordShown)}>show</span>
      {errors.confirmPassword && <p>{errors.confirmPassword.message}</p>}

      <Button type="submit">Submit</Button>
    </form>
  );
};;;;;

export default RegisterForm;
