import { call, put } from "redux-saga/effects";

import { setLoading } from "../Common/slice";

import axios from "../../axiosInstance";
import { PayloadAction } from "@reduxjs/toolkit";
import { IUserLoginForm } from "types";

interface IAuthResult {
  access_token: string;
}

const registerUser = (action: PayloadAction<IUserLoginForm>) => {
  axios.post<IAuthResult>("/auth/register", {
    email: action.payload.email,
    password: action.payload.password,
  });
};
export function* registerSaga(
  action: PayloadAction<IUserLoginForm>
): Generator {
  yield put(setLoading(true));
  try {
    const result = yield call(registerUser(action));
    if (result) localStorage.setItem("Mtoken", result.access_token);
  } catch (err) {
    console.error("sagaERR registerSaga", err);
  } finally {
    yield put(setLoading(false));
  }
}

export function* loginSaga(action: PayloadAction<IUserLoginForm>): Generator {
  yield put(setLoading(true));
  try {
    const result: IAuthResult = yield axios.post("/auth/login", {
      email: action.payload.email,
      password: action.payload.password,
    });
    localStorage.setItem("Mtoken", result.access_token);
  } catch (err) {
    console.error("sagaERR registerSaga", err);
  } finally {
    yield put(setLoading(false));
  }
}
