import { useAppDispatch, useAppSelector } from "redux/hooks";
import { IFormValues } from "types/types";
import MovieForm from "components/common/MovieForm";
import { setEmail } from "redux/Auth/slice";
import { setRedirect } from "redux/Common/slice";
import { addMovie } from "redux/Movie/slice";
import { Fragment } from "react";

const AddMoviePage = () => {
  const emailStore = useAppSelector((state) => state.Auth.email);
  const dispatch = useAppDispatch();
  const submitForm = (data: IFormValues) => {
    if (!emailStore) {
      let email: string | null = localStorage.getItem("email");
      !!email
        ? dispatch(setEmail(email))
        : dispatch(setRedirect({ state: true, url: "/register" }));
      //////////// console.log('change last line')
    }
    console.log({ ...data, emailStore });
    dispatch(addMovie({ ...data, creator: emailStore }));
  };

  return (
    <Fragment>
      <h3>Edit Movie</h3>
      <MovieForm onSubmit={submitForm} />
    </Fragment>
  );
};
export default AddMoviePage;
