import { call, put } from "redux-saga/effects";

import { setLoading } from "../Common/slice";

import axios from "../../axiosInstance";
import { PayloadAction } from "@reduxjs/toolkit";
import { IUserLoginForm } from "types";

interface IAuthResult {
  access_token: string;
}
export interface ITodo {
  userId: number;
  id: number;
  title: string;
  completed: boolean;
}
const getTodos = () =>
  axios.get<ITodo[]>("https://jsonplaceholder.typicode.com/todos");

const registerUser = (action: PayloadAction<IUserLoginForm>) => {
  return axios.post<IAuthResult>("/auth/register", {
    email: action.payload.email,
    password: action.payload.password,
  });
};

export function* registerSaga(
  action: PayloadAction<IUserLoginForm>
): Generator {
  yield put(setLoading(true));
  try {
