import React, {
  Fragment,
  useRef,
  ComponentType,
  useState,
  useEffect,
} from "react";
import { Button, Modal, ModalBody, ModalFooter, ModalHeader } from "reactstrap";

import { setPopUp } from "redux/Common/slice";
import { useAppDispatch, useAppSelector } from "redux/hooks";
import classes from "./style.module.scss";

export default function WithPopUp<T>(WrappedComponent: ComponentType<T>) {
  const Component = (props: T) => {
    const sectionRef = useRef(null);

    const dispatch = useAppDispatch();

    const { popUpState, popUpButtons, popUpText } = useAppSelector(
      (state) => state.Common
    );

    const [state, setState] = useState<boolean>(false);

    const onClose = () => {
      dispatch(setPopUp({ state: false, text: "", buttons: [] }));
    };

    // const checkOnBlur = (e: React.MouseEvent<HTMLDivElement>) => {
    //   if (e.target === sectionRef.current) onClose();
    // };

    const toggleModal = () => {
      setState(!state);
      if (popUpState && state)
        dispatch(setPopUp({ state: false, text: "", buttons: [] }));
    };

    useEffect(() => {
      popUpState && toggleModal();
    }, [popUpState]);
    console.log("popUp", { popUpState, state });
    return (
      <Fragment>
        {/* {popUpState && ( */}
        {/* <Button color="danger" className={classes.BTN} onClick={toggleModal}>
          Click Me
        </Button>
        <Button onClick={toggleModal}>Modal</Button> */}
        <Modal
          centered
          isOpen={state && popUpState}
          // className={classes.Section}
          toggle={toggleModal}
          // onClick={checkOnBlur}
          // ref={sectionRef}
        >
          <ModalHeader toggle={toggleModal}>Modal</ModalHeader>
          <ModalBody>
            <span>{popUpText}</span>
          </ModalBody>
          <ModalFooter>
            <div className={classes.buttons}>
              {popUpButtons.map((item, index) => (
                <Button
                  key={index}
                  color={item.color}
                  size="md"
                  onClick={() =>
                    item.isCloseBTN ? toggleModal() : item.onClick()
                  }
                >
                  {item.value}
                </Button>
              ))}
            </div>
          </ModalFooter>
        </Modal>
        {/* )} */}
        <WrappedComponent {...props} />
      </Fragment>
    );
  };
  return Component;
}
