    navigate(`edit-movie/${id}`);
  };

  if (currentMovie)
    return (
      <Fragment>
        <CurrentMoviePage
          onDelete={deleteItem}
          onEdit={navigateToEditPage}
          currentMovie={currentMovie}
        />
      </Fragment>
    );
  return null;
};
export default MoviePage;
